import{_ as t,W as d,X as r,a2 as e}from"./framework-7d1f27fd.js";const a={},o=e('<h1 id="linux-基础" tabindex="-1"><a class="header-anchor" href="#linux-基础" aria-hidden="true">#</a> Linux 基础</h1><table><thead><tr><th>命令</th><th>用途</th></tr></thead><tbody><tr><td>uname --help</td><td>内核名称、内核版本、硬件名称、硬件平台、操作系统</td></tr><tr><td>cat /proc/cpuinfo</td><td>CPU 详细信息</td></tr><tr><td>env</td><td>环境变量</td></tr><tr><td>free --help</td><td>查看内存使用量和交换区使用量</td></tr><tr><td>df --help</td><td>查看文件系统信息</td></tr><tr><td>du --help</td><td>汇总目录文件使用情况</td></tr><tr><td>uptime</td><td>查看系统运行时间、用户数、负载</td></tr><tr><td>cat /proc/loadavg</td><td>查看系统负载</td></tr><tr><td>ifconfig</td><td>查看防火墙设置</td></tr><tr><td>route --help</td><td>查看路由表</td></tr><tr><td>netstat -lntp</td><td>查看所有监听端口</td></tr><tr><td>netstat -nap</td><td>grep 80(2755)</td></tr><tr><td>kill</td><td>进程终止</td></tr><tr><td>killall</td><td>根据名称终止进程</td></tr><tr><td>ps -ef</td><td>查看所有进程</td></tr><tr><td>top</td><td>显示进程状态</td></tr><tr><td>who</td><td>查看当前登录用户</td></tr><tr><td>last</td><td>查看用户登录日志</td></tr><tr><td>cut -d: -f1 /etc/passwd</td><td>查看系统所有用户</td></tr><tr><td>cut -d: -f1 /etc/group</td><td>查看系统所有组</td></tr><tr><td>crontab -l</td><td>查看当前用户计划任务</td></tr><tr><td>rpm -qa</td><td>查看所有安装的软件包</td></tr><tr><td>date</td><td>系统时间</td></tr><tr><td>reboot</td><td>系统重启</td></tr><tr><td>poweroff</td><td>关机</td></tr></tbody></table><p>文件复制</p>',3),n=[o];function c(l,p){return d(),r("div",null,n)}const i=t(a,[["render",c],["__file","02.Linux基础.html.vue"]]);export{i as default};
