const e=JSON.parse('{"key":"v-5fda6c60","path":"/interview/%E7%BD%91%E7%BB%9C/04.TCP%E7%B2%98%E5%8C%85%E6%8B%86%E5%8C%85.html","title":"TCP 粘包拆包","lang":"zh-CN","frontmatter":{"description":"TCP 粘包拆包 IP 协议解决了数据包（Packet）的路由和寻址，上层的 TCP 协议不再关注路由和寻址，那么 TCP 协议解决的是传输的可靠性和顺序问题，上层不需要关心数据能否传输到目标进程，只要写入 TCP 协议的缓冲区的数据，协议栈几乎都能保证数据的送达。 粘包问题 当应用层协议使用 TCP 协议传输数据时，TCP 协议可能会将应用层发送的数据分成多个包依次发送，而数据的接收方收到的数据段可能有多个『应用层数据包』组成，所以当应用层从 TCP 缓冲区中读取数据时，发现数据包是粘连的，就需要对收到的数据进行拆分(分包)。","head":[["meta",{"property":"og:url","content":"https://mister-hope.github.io/article/docs/interview/%E7%BD%91%E7%BB%9C/04.TCP%E7%B2%98%E5%8C%85%E6%8B%86%E5%8C%85.html"}],["meta",{"property":"og:site_name","content":"博客"}],["meta",{"property":"og:title","content":"TCP 粘包拆包"}],["meta",{"property":"og:description","content":"TCP 粘包拆包 IP 协议解决了数据包（Packet）的路由和寻址，上层的 TCP 协议不再关注路由和寻址，那么 TCP 协议解决的是传输的可靠性和顺序问题，上层不需要关心数据能否传输到目标进程，只要写入 TCP 协议的缓冲区的数据，协议栈几乎都能保证数据的送达。 粘包问题 当应用层协议使用 TCP 协议传输数据时，TCP 协议可能会将应用层发送的数据分成多个包依次发送，而数据的接收方收到的数据段可能有多个『应用层数据包』组成，所以当应用层从 TCP 缓冲区中读取数据时，发现数据包是粘连的，就需要对收到的数据进行拆分(分包)。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"TCP 粘包拆包\\",\\"image\\":[\\"\\"],\\"dateModified\\":null,\\"author\\":[]}"]]},"headers":[{"level":2,"title":"粘包问题","slug":"粘包问题","link":"#粘包问题","children":[]},{"level":2,"title":"短连接分包","slug":"短连接分包","link":"#短连接分包","children":[]},{"level":2,"title":"TCP 发送机制","slug":"tcp-发送机制","link":"#tcp-发送机制","children":[]},{"level":2,"title":"长连接分包","slug":"长连接分包","link":"#长连接分包","children":[]},{"level":2,"title":"复杂的分包","slug":"复杂的分包","link":"#复杂的分包","children":[{"level":3,"title":"总结","slug":"总结","link":"#总结","children":[]}]}],"git":{"createdTime":null,"updatedTime":null,"contributors":[]},"readingTime":{"minutes":5.32,"words":1596},"filePathRelative":"interview/网络/04.TCP粘包拆包.md","excerpt":"<h1> TCP 粘包拆包</h1>\\n<p>IP 协议解决了数据包（Packet）的<code>路由</code>和<code>寻址</code>，上层的 TCP 协议不再关注路由和寻址，那么 TCP 协议解决的是传输的<code>可靠性</code>和<code>顺序</code>问题，上层不需要关心数据能否传输到目标进程，只要写入 TCP 协议的缓冲区的数据，协议栈几乎都能保证数据的送达。</p>\\n<h2> 粘包问题</h2>\\n<p>当应用层协议使用 TCP 协议传输数据时，TCP 协议可能会将应用层发送的数据分成多个包依次发送，而数据的接收方收到的数据段可能有多个『应用层数据包』组成，所以当应用层从 TCP 缓冲区中读取数据时，发现数据包是粘连的，就需要对收到的数据进行拆分(分包)。</p>","autoDesc":true}');export{e as data};
